<?php
require_once __DIR__.'/../config.php';require_once __DIR__.'/../db.php';
if(!isset($_GET['tg_id']))die('Missing tg_id');$tg_id=intval($_GET['tg_id']);$user=getUserByTgId($tg_id);if(!$user)die('Unknown user');$user_id=$user['id'];
$backUrl='greeting.php?tg_id='.$tg_id;if(isset($_GET['return'],$_GET['monitor_id'],$_GET['group_id'])&&$_GET['return']==='group'){$backUrl='view_group_attendance.php'.'?tg_id='.intval($_GET['monitor_id']).'&group_id='.intval($_GET['group_id']);}
$today=date('Y-m-d');$weekStart=date('Y-m-d',strtotime('monday this week'));$monthStart=date('Y-m-01');
function fetchStats($pdo,$uid,$from,$to=null){$params=[':uid'=>$uid,':from'=>$from];$cond="a.date >= :from";if($to!==null){$cond.=" AND a.date <= :to";$params[':to']=$to;}$totalQ=$pdo->prepare("SELECT COUNT(*) FROM attendance a WHERE a.user_id=:uid AND $cond");$totalQ->execute($params);$total=(int)$totalQ->fetchColumn();$absQ=$pdo->prepare("SELECT COUNT(*) FROM attendance a WHERE a.user_id=:uid AND $cond AND a.present=0");$absQ->execute($params);$absent=(int)$absQ->fetchColumn();$motQ=$pdo->prepare("SELECT COUNT(*) FROM attendance a WHERE a.user_id=:uid AND $cond AND a.present=0 AND a.motivated=1");$motQ->execute($params);$motiv=(int)$motQ->fetchColumn();$lstQ=$pdo->prepare("SELECT a.date,s.time_slot,s.subject,s.type,a.motivation FROM attendance a JOIN schedule s ON s.id=a.schedule_id WHERE a.user_id=:uid AND $cond AND a.present=0 ORDER BY a.date DESC,s.time_slot");$lstQ->execute($params);$rows=$lstQ->fetchAll(PDO::FETCH_ASSOC);return['total'=>$total,'absent'=>$absent,'motivated'=>$motiv,'unmotiv'=>$absent-$motiv,'rows'=>$rows];}
function countClassDays($pdo,$uid,$from,$to){$q=$pdo->prepare("SELECT COUNT(DISTINCT a.date) FROM attendance a WHERE a.user_id=:uid AND a.date BETWEEN :from AND :to AND a.present=1");$q->execute([':uid'=>$uid,':from'=>$from,':to'=>$to]);return(int)$q->fetchColumn();}
function inclusiveDays($from,$to){$a=new DateTime($from);$b=new DateTime($to);return $a->diff($b)->days+1;}
function term_bounds(string $today):array{$dt=new DateTime($today);$y=(int)$dt->format('Y');$m=(int)$dt->format('n');if($m>=9||$m===1){if($m===1){$start=new DateTime(($y-1).'-09-01');$end=new DateTime($y.'-01-31');$label='Autumn '.($y-1).'/'.$y;}else{$start=new DateTime($y.'-09-01');$end=new DateTime(($y+1).'-01-31');$label='Autumn '.$y.'/'.($y+1);}}else{$start=new DateTime($y.'-02-01');$end=new DateTime($y.'-08-31');$label='Spring '.$y;}return[$start->format('Y-m-d'),$end->format('Y-m-d'),$label];}
[$termStart,$termEnd,$termLabel]=term_bounds($today);
$stats=['Today'=>fetchStats($pdo,$user_id,$today,$today),'This Week'=>fetchStats($pdo,$user_id,$weekStart,$today),'This Month'=>fetchStats($pdo,$user_id,$monthStart,$today),'All Time'=>fetchStats($pdo,$user_id,'1970-01-01',$today)];
$labMissQ=$pdo->prepare("SELECT COUNT(*) FROM attendance a JOIN schedule s ON s.id=a.schedule_id WHERE a.user_id=:uid AND a.present=0 AND s.type='lab'");$labMissQ->execute([':uid'=>$user_id]);$labMissCount=(int)$labMissQ->fetchColumn();$labFee=$labMissCount*50;
$subjRows=$pdo->prepare("SELECT s.subject,s.type,COUNT(*) AS total,SUM(a.present=0) AS absent FROM attendance a JOIN schedule s ON s.id=a.schedule_id WHERE a.user_id=:uid GROUP BY s.subject,s.type");$subjRows->execute([':uid'=>$user_id]);$subjStats=[];while($r=$subjRows->fetch(PDO::FETCH_ASSOC)){$sub=$r['subject'];$typ=$r['type'];$subjStats[$sub]['labels'][$typ]=['total'=>(int)$r['total'],'absent'=>(int)$r['absent']];if(!isset($subjStats[$sub]['overall'])){$subjStats[$sub]['overall']=['total'=>0,'absent'=>0];}$subjStats[$sub]['overall']['total']+=(int)$r['total'];$subjStats[$sub]['overall']['absent']+=(int)$r['absent'];}
$todayDT=new DateTime($today);$prevMonthEndDT=(clone $todayDT)->modify('last day of previous month');$termEndDT=new DateTime($termEnd);$limitDT=($prevMonthEndDT<$termEndDT)?$prevMonthEndDT:$termEndDT;$streakMonths=0;$evaluatedMonth=false;$streakStartStr=null;$streakEndStr=null;if($limitDT>=new DateTime($termStart)){$cur=new DateTime($termStart);$cur->modify('first day of this month');while($cur<=$limitDT){$start=$cur->format('Y-m-01');if($start<$termStart)$start=$termStart;$endDT=(clone $cur)->modify('last day of this month');if($endDT>$limitDT)$endDT=$limitDT;$end=$endDT->format('Y-m-d');$monthStats=fetchStats($pdo,$user_id,$start,$end);if($monthStats['total']>0){$evaluatedMonth=true;if($monthStats['absent']===0){if($streakMonths===0)$streakStartStr=$start;$streakEndStr=$end;$streakMonths++;}else{break;}}$cur->modify('first day of next month');}}
$toDateEnd=($todayDT<$termEndDT)?$todayDT->format('Y-m-d'):$termEndDT->format('Y-m-d');$termStats=fetchStats($pdo,$user_id,$termStart,$toDateEnd);$perfectTermToDate=($termStats['total']>0&&$termStats['absent']===0&&$todayDT>=$termEndDT);
$eggMsg1=["First month flawless‚Äînice start! üèÅüíØ","One down, many to go. Perfect start! üåü","First month with zero absences. Chef‚Äôs kiss. üëå"];
$eggMsgN=["Streak on fire: %d perfect months! üî•","Keeping it clean for %d months‚Äîrespect. üôå","%d-month perfection streak unlocked. üèÜ"];
$eggMsgTerm=["You just aced the entire term with 100% attendance. Legendary. üèÖ","Perfect term achieved‚Äîmodel student mode: ON. üìö‚ú®","Zero absences this term. That‚Äôs elite. üí™"];
$eggMessages=["Flawless month! 100% attendance unlocked. üèÜ","No absences detected. The attendance gods are pleased. üòá","Perfect streak achieved‚Äîkeep it rolling! üî•","Achievement: ‚ÄûNever Miss a Beat‚Äù. üíØ","Legendary consistency‚Äîrespect! üôå","You made the ‚ÄòAbsent‚Äô column feel lonely. üòÖ","Model student vibes detected. üìö‚ú®","Attendance on point. Coffee well earned. ‚òïüí™"];$eggMsgExtra=$eggMessages[array_rand($eggMessages)];
$showEgg=false;$eggTitle='';$eggBody='';if($perfectTermToDate){$showEgg=true;$eggTitle='üéâ Perfect Term: '.htmlspecialchars($termLabel,ENT_QUOTES).'!';$eggBody=$eggMsgTerm[array_rand($eggMsgTerm)];}elseif($streakMonths>=2){$showEgg=true;$eggTitle='üî• Perfect Attendance Streak';$fmt=$eggMsgN[array_rand($eggMsgN)];$eggBody=sprintf($fmt,$streakMonths);}elseif($streakMonths===1&&$evaluatedMonth){$afterWeek=false;if($streakEndStr){$firstMonthEnd=new DateTime($streakEndStr);$afterWeek=($todayDT>=(clone $firstMonthEnd)->modify('+7 days'));}$showEgg=true;if($afterWeek){$eggTitle='‚úÖ Perfect Attendance';$eggBody=$eggMsgExtra;}else{$eggTitle='‚úÖ Perfect First Month';$eggBody=$eggMsg1[array_rand($eggMsg1)];}}
$termSpanStart=$termStart;$termSpanEnd=min($toDateEnd,$termEnd);$daysCalendar=inclusiveDays($termSpanStart,$termSpanEnd);$daysClass=countClassDays($pdo,$user_id,$termSpanStart,$termSpanEnd);
$theme=(($_COOKIE['theme']??'light')==='dark')?'dark':'light';
?>
<!doctype html><html class="<?= $theme==='dark'?'dark-theme':'' ?>" lang="en"><head><meta charset="UTF-8"><meta name="viewport" content="width=device-width,initial-scale=1"><title>My Attendance</title><link rel="stylesheet" href="style.css"></head><body><br><div id="theme-switch"><label class="switch"><input id="theme-toggle" type="checkbox" <?= $theme==='dark'?'checked':'' ?>><span class="slider"></span></label><span id="theme-label"><?= $theme==='dark'?'Dark':'Light' ?></span></div><br><br><button class="btn-nav" onclick='location.href="<?= $backUrl ?>"'>‚Üê Back</button><h1>My Attendance</h1><?php if($showEgg):?><div class="card card--perfect" style="margin:12px 0;"><h3><?= $eggTitle ?></h3><p><?= htmlspecialchars($eggBody,ENT_QUOTES) ?></p><p style="opacity:.8"><small>Term window: <?= htmlspecialchars($termStart,ENT_QUOTES) ?> ‚Äî <?= htmlspecialchars($termSpanEnd,ENT_QUOTES) ?></small></p><p style="opacity:.9"><small>Days this term to date: <?= $daysCalendar ?> (class days: <?= $daysClass ?>)</small></p><?php if(!$perfectTermToDate&&$streakMonths>0):?><p style="opacity:.9"><small>Current streak: <?= $streakMonths ?> month<?= $streakMonths>1?'s':'' ?></small></p><?php endif;?></div><?php endif;?><div class="cards"><?php foreach($stats as $label=>$st):$rate=$st['total']?round(100*$st['absent']/$st['total'],1):0;?><div class="card"><h3><?= $label ?></h3><p><strong>Sessions:</strong> <?= $st['total'] ?></p><p><strong>Absent:</strong> <?= $st['absent'] ?></p><p style="margin-left:12px">‚Äì unmotivated: <?= $st['unmotiv'] ?></p><p style="margin-left:12px">‚Äì motivated: <?= $st['motivated'] ?></p><p><strong>Absence Rate:</strong> <?= $rate ?>%</p><?php if($label==='All Time'):?><hr><p><strong>Lab Misses:</strong> <?= $labMissCount ?></p><p><strong>Est. Fee:</strong> <?= $labFee ?> Lei</p><?php endif;?></div><?php endforeach;?></div><h2>By Subject Absence Rates</h2><table class="subj-table"><thead><tr><th>Subject</th><th>Curs Rate</th><th>Sem Rate</th><th>Lab Rate</th><th>Overall Rate</th></tr></thead><tbody><?php foreach($subjStats as $sub=>$data):$oTotal=$data['overall']['total'];$oAbsent=$data['overall']['absent'];$oRate=$oTotal?100*$oAbsent/$oTotal:0;?><tr><td><?= htmlspecialchars($sub,ENT_QUOTES) ?></td><?php foreach(['curs','sem','lab'] as $t):$dTotal=$data['labels'][$t]['total']??0;$dAbsent=$data['labels'][$t]['absent']??0;$dRate=$dTotal?(100*$dAbsent/$dTotal):0;?><td><?= $dAbsent ?>/<?= $dTotal ?> (<?= number_format($dRate,2) ?>%)</td><?php endforeach;?><td><?= $oAbsent ?>/<?= $oTotal ?> (<?= number_format($oRate,2) ?>%)</td></tr><?php endforeach;?></tbody></table><?php foreach($stats as $label=>$st):if(empty($st['rows']))continue;?><h2><?= $label ?> Absences</h2><table><thead><tr><th>Date</th><th>Time</th><th>Subject</th><th>Type</th><th>Reason</th></tr></thead><tbody><?php foreach($st['rows'] as $r):?><tr><td><?= htmlspecialchars($r['date'],ENT_QUOTES) ?></td><td><?= htmlspecialchars($r['time_slot'],ENT_QUOTES) ?></td><td><?= htmlspecialchars($r['subject'],ENT_QUOTES) ?></td><td><?= htmlspecialchars($r['type'],ENT_QUOTES) ?></td><td><?= $r['motivation']?htmlspecialchars($r['motivation'],ENT_QUOTES):'<em>none</em>' ?></td></tr><?php endforeach;?></tbody></table><?php endforeach;?><script src="script.js"></script></body></html>
